/**
 * Formats a phone value into brazilian common phone formats.
 * @example ```js
 * formatToPhone('11')
 * //=> '(11'
 *
 * formatToPhone('11971626')
 * //=> '(11) 9716-26'
 *
 * formatToPhone('11971626799')
 * //=> '(11) 9 7162-6799'
 * ```
 * @param value
 */
declare const formatToPhone: (value: string) => string;
export default formatToPhone;
